<script>
  'use strict';
  const api = require('../js/api');
  window.api = api;

  export default api.Vue.extend({
    name: 'demo-app',

    data() { return {
      compare: {
        left: {a:1,b:1,c:3,s:''},
        right: {a:1,b:2,d:3,s:''}
      }
    };},

    computed: {

      deltaHtml() {
        try {
          return api.formatters.html.format(
              api.jsondiffpatch.diff(this.compare.left, this.compare.right),
              this.compare.left
          );
        }
        catch (bug) {
          return JSON.stringify(bug);
        }
      }
    }

  });
</script>

<template>
    <div>
        <h2>Demo</h2>
        <fieldset><legend>Result</legend>
            <div v-html="deltaHtml"></div>
        </fieldset>
    </div>
</template>

